import { PromiseWithResolver } from './PromiseWithResolver';
export default function wait(duration = 0) {
    let resolver;
    const promise = new PromiseWithResolver((resolve) => {
        const timeoutId = setTimeout(resolve, duration);
        resolver = () => {
            clearTimeout(timeoutId);
            resolve();
        };
    });
    promise.resolve = resolver;
    return promise;
}
